DROP TABLE IF EXISTS `cu_language_rich_content`;
CREATE TABLE `cu_language_rich_content` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `label` varchar(255) DEFAULT NULL,
  `content` text,
  `language` varchar(10) NOT NULL DEFAULT '',
  PRIMARY KEY (`id`),
  UNIQUE KEY `unique` (`label`,`language`)
) ENGINE=InnoDB AUTO_INCREMENT=2 DEFAULT CHARSET=utf8;

DROP TABLE IF EXISTS `cu_menu_item_type`;
CREATE TABLE `cu_menu_item_type` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `name` varchar(45) DEFAULT NULL,
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=7 DEFAULT CHARSET=utf8 COMMENT='Content menu types.\nExample:  [1, none], [2, autogenerated],';

DROP TABLE IF EXISTS `cu_menu_items`;
CREATE TABLE `cu_menu_items` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `title` varchar(255) NOT NULL,
  `alias` varchar(255) NOT NULL,
  `title_tab` varchar(255) NOT NULL DEFAULT '',
  `highlight` int(11) DEFAULT '0',
  `image` varchar(255) DEFAULT NULL,
  `view` int(11) DEFAULT NULL,
  `description` text,
  `menu_item_type_id` int(11) NOT NULL,
  `menu_item_params` varchar(255) DEFAULT NULL,
  `parent_id` int(11) DEFAULT NULL,
  `menus_id` int(11) NOT NULL,
  `order` int(11) DEFAULT NULL,
  `language` varchar(10) DEFAULT '',
  `enabled` int(11) NOT NULL DEFAULT '1',
  PRIMARY KEY (`id`),
  UNIQUE KEY `unique` (`alias`,`menus_id`),
  KEY `fk_cu_menu_item_type` (`menu_item_type_id`),
  KEY `fk_cu_menu` (`menus_id`)
) ENGINE=InnoDB AUTO_INCREMENT=123 DEFAULT CHARSET=utf8 COMMENT='Content menu items. Parent_id: Relation about the positions';

DROP TABLE IF EXISTS `cu_menus`;
CREATE TABLE `cu_menus` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `name` varchar(45) NOT NULL,
  `description` varchar(250) DEFAULT NULL,
  `language` varchar(45) DEFAULT NULL,
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=4 DEFAULT CHARSET=utf8;

DROP TABLE IF EXISTS `cu_permissions`;
CREATE TABLE `cu_permissions` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `name` varchar(50) DEFAULT NULL,
  `group` int(11) DEFAULT '1',
  `accept_default_value` int(11) DEFAULT '0',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=12 DEFAULT CHARSET=utf8 COMMENT='Content permissions.\nExample: [1, consult] , [2, insert]';

DROP TABLE IF EXISTS `cu_permissions_data`;
CREATE TABLE `cu_permissions_data` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `group` int(11) DEFAULT NULL COMMENT 'group_permission_id',
  `reference` varchar(100) DEFAULT NULL,
  `data` text,
  PRIMARY KEY (`id`),
  UNIQUE KEY `unique` (`reference`,`group`)
) ENGINE=InnoDB AUTO_INCREMENT=782 DEFAULT CHARSET=utf8;

DROP TABLE IF EXISTS `cu_permissions_group`;
CREATE TABLE `cu_permissions_group` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `name` varchar(255) DEFAULT NULL,
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=6 DEFAULT CHARSET=utf8;

DROP TABLE IF EXISTS `cu_permissions_values`;
CREATE TABLE `cu_permissions_values` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `value` varchar(50) DEFAULT NULL,
  `permission` varchar(255) DEFAULT NULL,
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=27 DEFAULT CHARSET=utf8;

DROP TABLE IF EXISTS `cu_tables`;
CREATE TABLE `cu_tables` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `table_name` varchar(45) NOT NULL,
  `params` text NOT NULL,
  PRIMARY KEY (`id`),
  UNIQUE KEY `table_name_UNIQUE` (`table_name`)
) ENGINE=InnoDB AUTO_INCREMENT=37 DEFAULT CHARSET=utf8 COMMENT='Content the table info. Example: [1, Products, {field name:';

DROP TABLE IF EXISTS `cu_tables_log`;
CREATE TABLE `cu_tables_log` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `user_id_creator` int(11) DEFAULT NULL,
  `user_id_update` int(11) DEFAULT NULL,
  `table_name` varchar(100) NOT NULL DEFAULT '',
  `reference_id` int(11) DEFAULT NULL,
  `date_registered` datetime DEFAULT NULL,
  `date_update` datetime DEFAULT NULL,
  `user_id_updating` int(11) DEFAULT NULL,
  `date_updating` datetime DEFAULT NULL,
  `deleted` int(11) DEFAULT NULL,
  PRIMARY KEY (`id`),
  UNIQUE KEY `table_name_UNIQUE` (`table_name`,`reference_id`)
) ENGINE=InnoDB AUTO_INCREMENT=58 DEFAULT CHARSET=utf8;

DROP TABLE IF EXISTS `cu_user_groups`;
CREATE TABLE `cu_user_groups` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `name` varchar(45) NOT NULL,
  `site_login` int(11) NOT NULL DEFAULT '1',
  `admin_login` int(11) NOT NULL DEFAULT '0',
  `super_admin_login` int(11) NOT NULL DEFAULT '0',
  `enabled` int(11) NOT NULL DEFAULT '1',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=5 DEFAULT CHARSET=utf8;

DROP TABLE IF EXISTS `cu_users`;
CREATE TABLE `cu_users` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `name` varchar(45) NOT NULL,
  `email` varchar(45) NOT NULL,
  `username` varchar(100) NOT NULL DEFAULT '',
  `password` varchar(500) NOT NULL DEFAULT '',
  `image` varchar(255) DEFAULT NULL,
  `user_group_id` int(11) NOT NULL,
  `enabled` int(11) NOT NULL DEFAULT '1',
  `date_registered` date DEFAULT NULL,
  PRIMARY KEY (`id`),
  UNIQUE KEY `username` (`username`)
) ENGINE=InnoDB AUTO_INCREMENT=3 DEFAULT CHARSET=utf8;

DROP TABLE IF EXISTS `cu_views`;
CREATE TABLE `cu_views` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `name` varchar(255) DEFAULT NULL,
  `description` text,
  `file_path` varchar(255) DEFAULT NULL,
  `screen` varchar(255) DEFAULT NULL,
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=3 DEFAULT CHARSET=utf8;

